mapper.select:
SELECT A.BOARD_NO AS BOARD_NO , A.TITLE AS TITLE , A.CONTENT AS CONTENT , A.WRITER AS WRITER , A.WRITE_DATE AS WRITE_DATE , A.LIKE_CNT AS LIKE_CNT , A.VIEW_CNT AS VIEW_CNT , A.IS_DEL AS IS_DEL , A.CATEGORY_CD AS CATEGORY_CD , B.CATEGORY_NM AS CATEGORY_NM , A.MENU_CD AS MENU_CD FROM BOARD A LEFT OUTER JOIN CATEGORY B ON A.CATEGORY_CD = B.CATEGORY_CD WHERE A.IS_DEL = FALSE
AND A.TITLE LIKE CONCAT('%', #{keyword}, '%')
AND A.WRITER LIKE CONCAT('%', #{keyword}, '%')
AND (A.TITLE LIKE CONCAT('%', #{keyword}, '%') OR A.CONTENT LIKE CONCAT('%', #{keyword}, '%'))
AND A.CATEGORY_CD = #{subCategory}
AND B.P_CD = #{category}
ORDER BY A.WRITE_DATE DESC , A.BOARD_NO DESC LIMIT #{size} OFFSET #{offset};
