mapper.select:
SELECT A.R_TOKEN
  FROM USERS A
 WHERE A.USER_ID = #{user_id}

mapper.insert:
INSERT
  INTO USERS (
			  USER_ID
			  , USER_PW
			  , USER_NM
			  , MAIL	
	 ) VALUES (
	 		  #{user_id}
	 		  , #{user_pw}
	 		  , #{user_nm}
	 		  , #{mail}
				 )

mapper.update:
UPDATE USERS A
   SET A.R_TOKEN = #{r_token}
 WHERE A.USER_ID = #{user_id}

UPDATE USERS A
   SET A.R_TOKEN = ''
 WHERE A.USER_ID = #{user_id}
