mapper.select:
SELECT A.USER_ID AS USER_ID 
  FROM USERS A
 WHERE A.USER_ID = #{user_id}
   AND A.USER_PW = #{user_pw}

mapper.insert:
INSERT
  INTO USERS (
			  USER_ID
			  , USER_PW
			  , USER_NM
			  , MAIL	
	 ) VALUES (
	 		  #{user_id}
	 		  , #{user_pw}
	 		  , #{user_nm}
	 		  , #{mail}
				 )
